import React, { useState, useEffect } from 'react';
import './HomeSales.css'; // Assuming this is your CSS file

function HomeSales() {
  // Step 2: Create state to store the JSON data
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Step 3: Fetch and load the JSON data
    fetch('./lists.json') // Adjust the path to your JSON file as needed
      .then((response) => response.json())
      .then((data) => setProducts(data));
  }, []); // Empty dependency array means this effect runs once on component mount

  return (
    <div className="container">
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Barcode</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {/* Step 4: Map over the JSON data to display it */}
          {products.map((product, index) => (
            <tr className="product" key={index}>
              <td>{product.name}</td>
              <td>{product.barcode}</td>
              <td>${product.price}</td>
              <td>
                <button className="btn btn-outline-success">Add to Cart</button>
                <button className="btn btn-outline-primary">More Info</button>
                <button className="btn btn-success">Buy Now</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default HomeSales;
