import React, { useState, useEffect } from 'react';
import './HomeSales.css';
import listdata from '../lists';
import Modal from 'react-modal';

Modal.setAppElement('#root');

const HomeSales = () => {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  useEffect(() => {
    setProducts(listdata);
  }, []);

  const openModal = (product) => {
    setSelectedProduct(product);
    setModalIsOpen(true);
    setTimeout(() => {
      setModalIsOpen(false);
    }, 2000);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const buy = () => {
    console.log('buy clicked');
    alert('bought');
  };

  return (
    <div className="container">
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Barcode</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr className="product" key={index}>
              <td>{product.name}</td>
              <td>{product.barcode}</td>
              <td>${product.price}</td>
              <td>
                <button className="btn btn-outline-success" onClick={() => openModal(product)}>Add to Cart</button>
                <button className="btn btn-outline-primary">More Info</button>
                <button className="btn btn-success" onClick={buy}>Buy Now</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Product Details"
        className="modal-content"
        overlayClassName="modal-overlay"
      >
        {selectedProduct && (
          <>
            <h2>{selectedProduct.name}</h2>
            <p>Price: ${selectedProduct.price}</p>
          </>
        )}
      </Modal>
    </div>
  );
}

export default HomeSales;
