import React, { useState, useEffect } from 'react';
import './HomeSales.css';
import listdata from '../lists'


const HomeSales = () => {
  const [products, setProducts] = useState(listdata);

  const AddToCart = () => {
    const productName = document.getElementById("productName");
    const productPrice = document.getElementById("productPrice");

    const popupBox = document.createElement("div");
    popupBox.className = "popup-box";

    popupBox.innerHTML = `<p><strong>${productName.textContent}</strong></p><p>Price: ${productPrice.textContent}</p>`;

    document.body.appendChild(popupBox);

    popupBox.style.display = "block";

    setTimeout(() => {
      popupBox.style.display = "none";
    }, 2000);
  }

  const buy = () => {
    console.log('buy clicked')
    alert('bought')
  };

  return (
    <div className="container">
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Barcode</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {}
          {products.map((product, index) => (
            <tr className="product" key={index}>
              <td>{product.name}</td>
              <td>{product.barcode}</td>
              <td>${product.price}</td>
              <td>
                <button className="btn btn-outline-success" onClick={AddToCart}>Add to Cart</button>
                <button className="btn btn-outline-primary">More Info</button>
                <button className="btn btn-success" onClick={buy}>Buy Now</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default HomeSales;
