import React from "react";

function SideCart({ items, totalItems, total, packaging, onPopup, onThankPopup }) {
  return (
    <div className="side-cart">
      <h2>Your Cart</h2>
      <ul>
        {items.map((item, id) => (
          <li key={id}>
            <div className="cart-item">
              <img src={item.src} alt={item.name} />
              <div className="item-details">
                <h3>{item.name}</h3>
                <p>Price: ${item.cost.toFixed(2)}</p>
              </div>
            </div>
          </li>
        ))}
      </ul>
      <div className="cart-summary">
        <p>Total Items: {totalItems}</p>
        <p>Total Cost: ${total.toFixed(2)}</p>
        <p>Packaging: ${packaging.toFixed(2)}</p>
        <button onClick={onPopup}>Proceed to Checkout</button>
      </div>
    </div>
  );
}

export default SideCart;
