import React, { useState } from "react";
import "./ItemList.css";
import Drawer_orders from "../OrderPage/Drawer";

function ItemList(props) {
  const { items, onAdd, onRemove } = props;
  const [isDrawerOpen, setIsDrawerOpen] = useState(false); // State to track if the drawer should be open

  return (
    <div className="list-container">
      {items.map((item, id) => (
        <div className="item-container" key={id}>
          <img src={item.src} alt={item.name} className="img" />
          <h3>{item.name}</h3>
          <b>${item.cost.toFixed(2)}</b>
          <br></br>
          {/* Render the Add To Cart button and open the drawer when clicked */}
          <button
            type="button"
            className="buyButton"
            onClick={() => setIsDrawerOpen(true)}
          >
            <span>Add To Cart</span>
          </button>
          <button
            type="button"
            className="buyButton"
            onClick={() => onRemove(item.name, item.cost)}
          >
            <span> Buy Now</span>
          </button>
        </div>
      ))}
      {/* Conditionally render the Drawer_orders component */}
      {isDrawerOpen && (
        <Drawer_orders
          items={items}
          cost={items.reduce((total, item) => total + item.cost, 0)}
        />
      )}
    </div>
  );
}

export default ItemList;
