import React, { useState, useEffect } from 'react';
import './HomeSales.css';
import listdata from '../lists';

const HomeSales = () => {
  const [products, setProducts] = useState([]);
  const [popupVisible, setPopupVisible] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState({});

  const openPopup = (product) => {
    setSelectedProduct(product);
    setPopupVisible(true);

    setTimeout(() => {
      setPopupVisible(false);
    }, 2000);
  };

  useEffect(() => {
    setProducts(listdata);
  }, []);

  const buy = () => {
    console.log('buy clicked')
    alert('bought')
  };

  return (
    <div className="container">
      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Barcode</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr className="product" key={index}>
              <td>{product.name}</td>
              <td>{product.barcode}</td>
              <td>${product.price}</td>
              <td>
                <button className="btn btn-outline-success" onClick={() => openPopup(product)}>Add to Cart</button>
                <button className="btn btn-outline-primary">More Info</button>
                <button className="btn btn-success" onClick={buy}>Buy Now</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {popupVisible && (
        <div className="popup-box">
          <p><strong>{selectedProduct.name}</strong></p>
          <p>Price: ${selectedProduct.price}</p>
        </div>
      )}
    </div>
  );
}

export default HomeSales;
