import React from 'react';
import { mount } from 'enzyme';
import FileFieldCell from '../fileFieldCell';
describe('<FileFieldCell />', function () {
  it('should return the same value if value is sent as param', function () {
    var returnValue = FileFieldCell({
      value: 'test-value'
    });
    expect(returnValue).toBe('test-value');
  });
  it('should have assign field content when no value is sent as param', function () {
    var component = mount(React.createElement(FileFieldCell, null));
    expect(component.text()).toBe('Not assigned yet');
  });
});